'use strict';

var NOTIFY_TYPE = 'chrome';

var http = require('http');

var helloCommand = {
  "command" : "ping",
  "data"    : ""
};

var notifyCommand = {
  "command" : "notification",
  "data"    : {
    "type"    : "basic",
    "title"   : "",
    "message" : "",
    // Grunt icon
    "iconUrl" : "data:image/png;base64,"
  }
};

function httpRequest(options, data, cb) {
  var port = (options.platform && options.platform.port) || 8989;

  var request_options = {
    host    : '127.0.0.1',
    port    : port,
    method  : 'POST',
    headers : {
        'Content-Type'   : 'application/json',
        'Content-Length' : data.length
    }
  };

  var request = http.request(request_options, function(res) {        

  	var responseString = "";

    res.setEncoding('utf8');

    res.on('data', function (chunk) { 
      responseString += chunk;
    });

    res.on('end', function () { 
      cb(responseString);
    });

  });

  request.on('error', function(e) {
  	cb(e.toString(), true);
  });

  request.write(data);
  request.end();
}

function supported(options, cb) {  
  httpRequest(options, JSON.stringify( helloCommand ), function( result, isError ) {      
    if ( !isError && result === "chrome-notification-server" ) {
      cb(true);      
    }
    else {
      cb(false);            
    }

    options.debug({
  	  chrome : isError ? result + ". To use chrome as notification server you should install chrome application: https://github.com/krustnic/chrome-notification-server" : "Will use Chrome notifications"
    });
  });  
}

function notify(options, cb) {
  var notifyCmd = JSON.parse( JSON.stringify( notifyCommand ) );
  notifyCmd.data.title   = options.title;
  notifyCmd.data.message = options.message;

  // For debug don't output long iconUrl data uri
  var debugNotifyCmd = JSON.parse( JSON.stringify( notifyCmd ) );
  if ( typeof notifyCmd.data.iconUrl === "string" ) {
  	debugNotifyCmd.data.iconUrl = "<DATA URI>";
  }

  options.debug({
  	request : JSON.stringify( debugNotifyCmd )
  });

  httpRequest(options, JSON.stringify( notifyCmd ), function( result, isError ) {    
  	if ( isError ) {
  		cb(result);	
  	}
  	else {
  		cb();
  	}    

    options.debug({
	  response : result
	});

  });    
}

module.exports = {
  name      : NOTIFY_TYPE,
  supported : supported,
  notify    : notify
};